import React from 'react';

function AppealModal({ appeal, onClose }) {
  if (!appeal) return null;

  const getStatusColor = (status) => {
    switch(status) {
      case 'В работе': return '#f39c12';
      case 'Решено': return '#27ae60';
      case 'Отклонено': return '#e74c3c';
      default: return '#95a5a6';
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Обращение #{appeal.id}</h2>
          <button className="close-btn" onClick={onClose}>×</button>
        </div>
        
        <div className="modal-body">
          <div className="info-grid">
            <div className="info-item">
              <span className="info-label">Категория:</span>
              <span className="info-value">{appeal.category}</span>
            </div>
            
            <div className="info-item">
              <span className="info-label">Статус:</span>
              <span 
                className="info-value status"
                style={{ color: getStatusColor(appeal.status) }}
              >
                {appeal.status}
              </span>
            </div>
            
            <div className="info-item">
              <span className="info-label">Адрес:</span>
              <span className="info-value">{appeal.address}</span>
            </div>
            
            <div className="info-item">
              <span className="info-label">Дата регистрации:</span>
              <span className="info-value">{appeal.created_at}</span>
            </div>
          </div>
          
          <div className="description-section">
            <h3>Описание проблемы</h3>
            <div className="description-text">
              {appeal.description}
            </div>
          </div>
          
          <div className="coordinates-section">
            <h3>Координаты</h3>
            <div className="coordinates-grid">
              <div className="coordinate">
                <span className="coord-label">Широта:</span>
                <span className="coord-value">{appeal.latitude}</span>
              </div>
              <div className="coordinate">
                <span className="coord-label">Долгота:</span>
                <span className="coord-value">{appeal.longitude}</span>
              </div>
            </div>
          </div>
        </div>
        
        <div className="modal-footer">
          <button className="close-button" onClick={onClose}>
            Закрыть
          </button>
        </div>
      </div>
      
      <style jsx>{`
        .modal-overlay {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background: rgba(0, 0, 0, 0.5);
          display: flex;
          justify-content: center;
          align-items: center;
          z-index: 1000;
          padding: 20px;
          animation: fadeIn 0.3s;
        }
        
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        
        .modal-content {
          background: white;
          border-radius: 12px;
          max-width: 600px;
          width: 100%;
          max-height: 90vh;
          overflow-y: auto;
          box-shadow: 0 10px 40px rgba(0, 0, 0, 0.2);
          animation: slideIn 0.3s;
        }
        
        @keyframes slideIn {
          from { transform: translateY(-50px); opacity: 0; }
          to { transform: translateY(0); opacity: 1; }
        }
        
        .modal-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 20px;
          border-bottom: 1px solid #eee;
          background: #f8f9fa;
          border-radius: 12px 12px 0 0;
        }
        
        .modal-header h2 {
          margin: 0;
          color: #2c3e50;
          font-size: 22px;
        }
        
        .close-btn {
          background: none;
          border: none;
          font-size: 28px;
          color: #7f8c8d;
          cursor: pointer;
          width: 40px;
          height: 40px;
          display: flex;
          align-items: center;
          justify-content: center;
          border-radius: 50%;
          transition: all 0.3s;
        }
        
        .close-btn:hover {
          background: #e74c3c;
          color: white;
        }
        
        .modal-body {
          padding: 20px;
        }
        
        .info-grid {
          display: grid;
          grid-template-columns: repeat(2, 1fr);
          gap: 15px;
          margin-bottom: 25px;
        }
        
        .info-item {
          display: flex;
          flex-direction: column;
          gap: 5px;
        }
        
        .info-label {
          font-size: 14px;
          color: #7f8c8d;
          font-weight: 600;
        }
        
        .info-value {
          font-size: 16px;
          color: #2c3e50;
        }
        
        .info-value.status {
          font-weight: bold;
        }
        
        .description-section {
          margin-bottom: 25px;
        }
        
        .description-section h3 {
          margin: 0 0 15px 0;
          color: #2c3e50;
          font-size: 18px;
        }
        
        .description-text {
          background: #f8f9fa;
          padding: 20px;
          border-radius: 8px;
          line-height: 1.6;
          color: #34495e;
        }
        
        .coordinates-section h3 {
          margin: 0 0 15px 0;
          color: #2c3e50;
          font-size: 18px;
        }
        
        .coordinates-grid {
          display: grid;
          grid-template-columns: repeat(2, 1fr);
          gap: 15px;
        }
        
        .coordinate {
          background: #f8f9fa;
          padding: 15px;
          border-radius: 8px;
          display: flex;
          justify-content: space-between;
        }
        
        .coord-label {
          color: #7f8c8d;
          font-weight: 600;
        }
        
        .coord-value {
          color: #2c3e50;
          font-family: monospace;
        }
        
        .modal-footer {
          padding: 20px;
          border-top: 1px solid #eee;
          display: flex;
          justify-content: flex-end;
        }
        
        .close-button {
          background: #3498db;
          color: white;
          border: none;
          padding: 12px 30px;
          border-radius: 8px;
          font-size: 16px;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s;
        }
        
        .close-button:hover {
          background: #2980b9;
        }
        
        @media (max-width: 768px) {
          .info-grid {
            grid-template-columns: 1fr;
          }
          
          .coordinates-grid {
            grid-template-columns: 1fr;
          }
          
          .modal-content {
            max-height: 95vh;
          }
        }
        
        @media (max-width: 480px) {
          .modal-header {
            padding: 15px;
          }
          
          .modal-header h2 {
            font-size: 18px;
          }
          
          .modal-body {
            padding: 15px;
          }
          
          .close-button {
            width: 100%;
          }
        }
      `}</style>
    </div>
  );
}

export default AppealModal;
