import React from 'react';
import './Filters.css';

function Filters({ filters, onStatusChange, onSearchChange }) {
  return (
    <div className="filters">
      <div className="filter-group">
        <label>Поиск:</label>
        <input
          type="text"
          placeholder="По категории или адресу..."
          value={filters.search}
          onChange={(e) => onSearchChange(e.target.value)}
          className="search-input"
        />
      </div>
      
      <div className="filter-group">
        <label>Статус:</label>
        <div className="status-buttons">
          <button
            className={filters.status === '' ? 'active' : ''}
            onClick={() => onStatusChange('')}
          >
            Все
          </button>
          <button
            className={filters.status === 'В работе' ? 'active status-work' : ''}
            onClick={() => onStatusChange('В работе')}
          >
            В работе
          </button>
          <button
            className={filters.status === 'Решено' ? 'active status-resolved' : ''}
            onClick={() => onStatusChange('Решено')}
          >
            Решено
          </button>
          <button
            className={filters.status === 'Отклонено' ? 'active status-rejected' : ''}
            onClick={() => onStatusChange('Отклонено')}
          >
            Отклонено
          </button>
        </div>
      </div>
      
      <style jsx>{`
        .filters {
          display: flex;
          gap: 30px;
          margin-bottom: 20px;
          padding: 20px;
          background: white;
          border-radius: 8px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }
        
        .filter-group {
          flex: 1;
        }
        
        label {
          display: block;
          margin-bottom: 10px;
          font-weight: bold;
          color: #333;
        }
        
        .search-input {
          width: 100%;
          padding: 10px;
          border: 1px solid #ddd;
          border-radius: 4px;
          font-size: 16px;
        }
        
        .status-buttons {
          display: flex;
          gap: 10px;
        }
        
        .status-buttons button {
          padding: 10px 15px;
          border: 1px solid #ddd;
          background: white;
          border-radius: 4px;
          cursor: pointer;
          font-size: 14px;
        }
        
        .status-buttons button:hover {
          background: #f5f5f5;
        }
        
        .status-buttons .active {
          color: white;
        }
        
        .status-work.active {
          background: #f39c12;
          border-color: #f39c12;
        }
        
        .status-resolved.active {
          background: #27ae60;
          border-color: #27ae60;
        }
        
        .status-rejected.active {
          background: #e74c3c;
          border-color: #e74c3c;
        }
        
        @media (max-width: 768px) {
          .filters {
            flex-direction: column;
            gap: 20px;
          }
          
          .status-buttons {
            flex-wrap: wrap;
          }
          
          .status-buttons button {
            flex: 1;
          }
        }
      `}</style>
    </div>
  );
}

export default Filters;
