import React from 'react';

function AppealsTable({ appeals, onSelect }) {
  const getStatusColor = (status) => {
    switch(status) {
      case 'В работе': return '#f39c12';
      case 'Решено': return '#27ae60';
      case 'Отклонено': return '#e74c3c';
      default: return '#95a5a6';
    }
  };

  return (
    <div className="table-container">
      <table className="appeals-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Категория</th>
            <th>Адрес</th>
            <th>Статус</th>
            <th>Дата регистрации</th>
          </tr>
        </thead>
        <tbody>
          {appeals.length === 0 ? (
            <tr>
              <td colSpan="5" style={{ textAlign: 'center', padding: '20px' }}>
                Нет данных
              </td>
            </tr>
          ) : (
            appeals.map(appeal => (
              <tr 
                key={appeal.id} 
                onClick={() => onSelect(appeal)}
                className="table-row"
              >
                <td>{appeal.id}</td>
                <td>{appeal.category}</td>
                <td>{appeal.address}</td>
                <td>
                  <span 
                    className="status-badge"
                    style={{ backgroundColor: getStatusColor(appeal.status) }}
                  >
                    {appeal.status}
                  </span>
                </td>
                <td>{appeal.created_at}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
      
      <style jsx>{`
        .table-container {
          overflow-x: auto;
        }
        
        .appeals-table {
          width: 100%;
          border-collapse: collapse;
          background: white;
        }
        
        .appeals-table th {
          background: #2c3e50;
          color: white;
          padding: 15px;
          text-align: left;
          font-weight: 600;
        }
        
        .appeals-table td {
          padding: 15px;
          border-bottom: 1px solid #eee;
        }
        
        .table-row {
          cursor: pointer;
          transition: background 0.3s;
        }
        
        .table-row:hover {
          background: #f8f9fa;
        }
        
        .status-badge {
          display: inline-block;
          padding: 5px 10px;
          border-radius: 4px;
          color: white;
          font-size: 12px;
          font-weight: bold;
        }
        
        @media (max-width: 768px) {
          .appeals-table th,
          .appeals-table td {
            padding: 10px;
            font-size: 14px;
          }
          
          .status-badge {
            padding: 3px 8px;
            font-size: 11px;
          }
        }
      `}</style>
    </div>
  );
}

export default AppealsTable;
